import { createStore } from 'redux'
import { createSlice } from '@reduxjs/toolkit'

const initialState = { counter: 0, showCounter: true }

//1-every slice need a name
//2- insitial State
const counterSlic = createSlice({
    name: 'counter',
    initialState: initialState,
    //reducers are functions that can update the state
    reducers: {
        increment(state) {
            state.counter++;
        },
        decrement(state) {
            state.counter--;
        },
        increase(state, action) {
            state.counter = state.counter + action.amount;
        },
        toggleCounter(state) {
            state.showCounter = !state.showCounter;
        }
    }
})


//Store is created by passing the reducer function to createStore
const store = createStore(counterSlic.reducer);

export default store;
